sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
	c.template = "";

	c.templatechoice = {
		hideSearch: true
	};


	c.valueSelected = function(selectedValue)
	{
		c.data.selectedTemplate = selectedValue;
		c.server.update();
	}



};

sp_widget.script:
(function() {

	//create a place in the data object to hold your values
    data.templates = [];

		 var r = new sn_ws.RESTMessageV2('global.JsonPlaceHolder-widget', 'Users');
		 var response = r.execute();
		 var responseBody = response.getBody();
		 var httpStatus = response.getStatusCode();

			var userList = JSON.parse(responseBody);

			for(var i = 0 ; i < userList.length; i++)
				{
					//console.log(userList[i])
					var temp = {}; 
						temp.label = userList[i].name;
						temp.value = userList[i].email;
						data.templates.push(temp);
				}


	if(input){

		console.log(input.selectedTemplate)
	}


		})();
