sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;

	c.send = function(){
		c.data.channelsend= $("#channelsend").val();
		c.data.template = $("#templatesend").val();
		c.data.action = "sendMessage";

		c.server.update().then(function(){

			c.data.action = undefined;
			c.data.mobile= "";
			c.data.channelsend= "";
			c.data.template= "";
			c.data.varone= "";
			c.data.vartwo= "";
			c.data.varthree= "";
		})
	}

};

