sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */


	if(input.action == "sendMessage"){
		console.log(input.varone)
		console.log(input.vartwo)
		console.log(input.varthree)

		var r = new sn_ws.RESTMessageV2('global.Notification-Widget', 'Notification');

    r.setStringParameterNoEscape('msg_template_id', input.template);
    r.setStringParameterNoEscape('channel_id', input.channelsend);
    r.setStringParameterNoEscape('recipient_phone_number', input.mobile);

    r.setStringParameterNoEscape('PlaceHolderValue1', '11th November 2023');
    r.setStringParameterNoEscape('PlaceHolderValue3', '10:20 AM');
    r.setStringParameterNoEscape('PlaceHolderValue2', 'Heathrow Terminal 20');
		r.setStringParameterNoEscape('PlaceHolderLabel1', '{{flightDate}}');
 		r.setStringParameterNoEscape('PlaceHolderLabel2', '{{flightTerminal}}'); 
	  r.setStringParameterNoEscape('PlaceHolderLabel3', '{{flightTime}}');

		var response = r.execute();
		var responseBody = response.getBody();
		var httpStatus = response.getStatusCode();

		 console.log(httpStatus)
		 console.log(response)
		 console.log(responseBody)
	}
})();
